svg.script:
var BLOTTO_APP = {};

BLOTTO_APP.states = {
	asigningTroops: 1,
	fight: 2,
	result: 3,
	invalid: 4
};

BLOTTO_APP.onLoad = function() {
	BLOTTO_APP.currentState = this.states.asigningTroops;
};

BLOTTO_APP.onMouseDown = function(evt) {
	this.currentState++;
	this.currentState %= this.states.invalid;
	alert(this.currentState);
};
